mapper.insert:
/* [MemberMapper.xml] [join] [회원 가입] */
INSERT INTO MEMBER
(    email
   , mmbr_pw
   , crt_id
   , upd_id
)
VALUES
(    #{email}
   , SHA2(#{mmbrPw}, 256)
   , #{email}
   , #{email}
)

mapper.select:
/* [MemberMapper.xml] [login] [로그인 처리] */
SELECT email
     , mmbr_pw
  FROM MEMBER
 WHERE email = #{email}
   AND mmbr_pw = SHA2(#{mmbrPw}, 256)
