mapper.select:
/* [RlestMapper.xml] [rlestCount] [매물 전체 카운트] */
SELECT count(*)
  FROM real_estate
 WHERE delete_status_check = 0;

/* [RlestMapper.xml] [searchRlestList] [조건 검색한 매물 불러오기] */ SELECT rlest_num , brkr_id , rlest_adr , rlest_sort , deposit , monthly_rent , maintenance_charge , room_area , structure , direction , floor , total_floor , parking_check , elevator_check , move_in_day , post_title , rlest_intro , trans_status_check , delete_status_check , crt_id , DATE_FORMAT(crt_dttm, '%Y. %m. %d') AS crt_dttm , upd_id , upd_dttm FROM real_estate WHERE delete_status_check = 0
AND rlest_adr like CONCAT('%',#{realEstate.rlestAdr},'%')
AND rlest_sort = #{realEstate.rlestSort}
AND deposit = #{realEstate.deposit}
AND deposit = #{realEstate.monthlyRent}
AND structure = #{realEstate.structure}
ORDER BY trans_status_check DESC, crt_dttm DESC LIMIT #{paging.offset} OFFSET #{paging.limitStart}

SELECT count(rlest_num)
  FROM real_estate
 WHERE delete_status_check = 0


   AND rlest_adr like CONCAT('%',#{rlestAdr},'%')



   AND rlest_sort = #{rlestSort}



   AND deposit = #{deposit}



   AND deposit = #{monthlyRent}



   AND structure = #{structure}

/* [RlestMapper.xml] [getWishListCheck] [위시리스트 체크] */
SELECT count(email)
  FROM wish_list
 WHERE email = #{email}
   AND rlest_num = #{rlestNum}

mapper.insert:
/* [RlestMapper.xml] [wishListCtr] [위시리스트 추가] */
INSERT INTO wish_list
(    email
   , rlest_num
   , crt_id
)
values
(    #{email}
   , #{rlestNum}
   , #{email}
)

mapper.delete:
/* [RlestMapper.xml] [wishListDel] [위시리스트 삭제] */
DELETE
  FROM wish_list
 WHERE email = #{email}
   AND rlest_num = #{rlestNum}
